import React, {useState} from "react";
import { View, Text, SafeAreaView, ScrollView, StyleSheet, TouchableOpacity } from "react-native";
import { sliderItems } from "../../data";



export default function Slider () {

    return (
        <ScrollView showsHorizontalScrollIndicator = {true} style = {{display: "flex", flexDirection: "row"}}>
            <TouchableOpacity >
                <View style = {{
                    backgroundColor: "white",
                    height: "70px",
                    width: "100vw"
                }}>
                    First
                </View>
            </TouchableOpacity>

                <View style = {{
                    backgroundColor: "orange",
                    height: "70px",
                    width: "100vw",
                    marginLeft: "80vw"
                }}>
                    Second
                </View>
            
        </ScrollView>
    )

}
