import {createSlice} from "@reduxjs/toolkit"


const sideSlice = createSlice({
    name: "side",
    initialState: {
        open: false
    },
    reducers: {
        setOpen:(state, action) => {
            state.open = !state.open
        }
    }
})

export const {setOpen} = sideSlice.actions


export default sideSlice.reducer
