import React, { useState } from "react";
import { View, Text, SafeAreaView, ScrollView, StyleSheet, Image, Button, TouchableOpacity, FlatList } from "react-native";


export default function Product ({route}) {
    //const {item} = route.params
    //console.log(route.params.item.img)
    return (
        <ScrollView vertical style={{backgroundColor: "white"}}>
                <Image 
                source={{uri: `https://i.ibb.co/PGWZZKD/one.jpg`}}
                style = {styles.image}
                />
                <View style={{padding: 10}}> 
                    <Text style={{fontSize: 20, fontWeight: 600, marginButtom: 15}}>Demin jeans</Text>
                    <Text style={{fontSize: 18, marginBottom: 20}}>Lorem ipsum dolor sit amet consectetur adipisicing elit. Dolorum rem enim ratione odio animi quod facere eos. Accusantium amet iste magni. Repellendus ipsam voluptatum quae blanditiis assumenda omnis debitis magni!</Text>
                    <Text style={{fontSize: 20, fontWeight: 600}}>$ 20</Text>
                    <View style={{flexDirection: "row"}}>
                        <Text style={{fontSize: 18}}>Color <View ></View> <View></View> </Text>
                    </View>
                    <View style={{flexDirection: "row"}}>
                        <View style={{flexDirection: "row"}}>
                            <TouchableOpacity><Text>-</Text></TouchableOpacity>
                            <TouchableOpacity><Text>1</Text></TouchableOpacity>
                            <TouchableOpacity><Text>+</Text></TouchableOpacity>
                        </View>
                    </View>
                </View>
            
        </ScrollView> 
    )
}  

const styles = StyleSheet.create({
    image: {
        width: "100%",
        height: 300,
        marginBottom: 15
    },
    cart: {

    }
})
