import axios from "axios";
import React, { useEffect, useState } from "react";
import { View, Text, SafeAreaView, ScrollView, StyleSheet, Image, Button, TouchableOpacity, FlatList } from "react-native";
import { Dropdown } from 'react-native-element-dropdown';
import AntDesign from 'react-native-vector-icons/AntDesign';
import Products from "../components/home/Products";

export default function ProductList ({navigation, route:{cat}}) {
    
    const colors = [
        { label: 'white', value: 'white' },
        { label: 'black', value: 'black' },
        { label: 'red', value: 'red' },
        { label: 'blue', value: 'blue' },
        { label: 'yellow', value: 'yellow' },
        { label: 'green', value: 'green' },
    ]

    const sizes = [
        { label: 'XS', value: 'XS' },
        { label: 'S', value: 'S' },
        { label: 'M', value: 'M' },
        { label: 'L', value: 'L' },
        { label: 'X', value: 'X' },
    ]

    const [color, setColor] = useState(null);
    const [size, setSize] = useState(null)
    const [isFocus, setIsFocus] = useState(false)
    const [products, setProducts] = useState([])


    useEffect(() => {
        const getProducts = async () => {
            try{
                const res = await axios.get(cat ? `http://localhost:5000/api/products?category=${cat}` : `http://localhost:5000/api/products`)
                setProducts(res.data)
                console.log(products)
            } catch(err) {
                console.log(err)
            }

        }
        getProducts()
    }, [cat])


    return(
        <ScrollView vertical showsVerticalScrollIndicator = {true} style = {{backgroundColor: "white"}}>
            <Text style={{fontSize: 20, fontWeight: 600}}>Dresses</Text>
            <View style = {styles.container}>
                <View style={{flexDirection: "column"}}>
                <Text style = {{fontSize: 16, fontWeight: 600, marginBottom: 6}}>Colors</Text>
                <Dropdown
                    style={[styles.dropdown, isFocus && {borderColor: `${color}`}]}
                    data={colors}
                    value={color}
                    labelField="label"
                    valueField="color"
                    onFocus={() => setIsFocus(true)}
                    onBlur={() => setIsFocus(false)}
                    onChange={item => {
                        setColor(item.value)
                        setIsFocus(false)
                    }}
                />
                </View>

                <View style={{flexDirection: "column"}}>
                    <Text style = {{fontSize: 16, fontWeight: 600, marginBottom: 6}}>Sizes</Text>
                    <Dropdown
                        style={[styles.dropdown, isFocus && {borderColor: `black`}]}
                        data={sizes}
                        value={size}
                        labelField="label"
                        valueField="size"
                        onFocus={() => setIsFocus(true)}
                        onBlur={() => setIsFocus(false)}
                        onChange={item => {
                            setSize(item.value)
                            setIsFocus(false)
                        }}
                    />
                </View>

            </View>
            <Products navigation = {navigation} page="productList"/>

         </ScrollView>
             
    )
}


const styles = StyleSheet.create({
    container: {
      backgroundColor: 'white',
      padding: 6,
      display: "flex",
      flexDirection: "row",
      justifyContent: "space-evenly"
    //   width: "35%"

    },
    dropdown: {
      maxHeight: 50,
      borderColor: 'gray',
      borderWidth: 0.5,
      borderRadius: 8,
      paddingHorizontal: 8,
    },
    label: {

    }

})



