import React, { useState } from "react";
import { View, Text, SafeAreaView, ScrollView, StyleSheet, Image, TouchableOpacity, Pressable, Modal } from "react-native";

export default function Cart() {

    const [modalVisible, setModalVisible] = useState(true)

    const checkOutModal = () => {
        return(
            <ScrollView vertical showsVerticalScrollIndicator = {false}>
                {/* <View>hsmfvnd,sc4s5dajsdclk</View> */}
                <View style={styles.modalContainer}>
                    <View style={styles.modalCheckoutContainer}>

                        <Text style={styles.bag}>Your Bag</Text>
                        <View style={{borderBottomWidth: 2}}>
                            <Image 
                                style={styles.img}
                                source={{uri: `https://i.ibb.co/PGWZZKD/one.jpg`}}
                            />
                            <View style={{flexDirection: 'row', justifyContent: "space-evenly"}}>
                                <View>
                                    <Text style={{fontSize: 18, fontWeight: 100, marginBottom: 10}}>Kyle Long</Text>
                                    <Text style={{fontSize: 18, fontWeight: 100, marginBottom: 10}}>14257336729</Text>
                                    <Text style={{}}></Text>
                                    <Text style={{fontSize: 18, fontWeight: 100,}}>M</Text>
                                </View>
                                <View style={{flexDirection: "column", justifyContent: "space-evenly"}}>
                                    <View style={{flexDirection: "row", justifyContent: "space-evenly", marginTop: 1}}>
                                        <View style={{flexDirection: "row", marginTop: 1 }}>
                                            <TouchableOpacity><Text style = {[styles.change, {marginRight: 12}]}>-</Text></TouchableOpacity>
                                            <Text style={styles.quantity}>1</Text>
                                            <TouchableOpacity><Text style = {[styles.change, {marginLeft: 12}]}>+</Text></TouchableOpacity>
                                        </View>
                                    </View>
                                    <View><Text style={{fontSize: 18, textAlign: "center"}} >$ 20</Text></View>
                                </View>
                                
                            </View>

                            
                        </View>
                    </View>
                </View>
            </ScrollView>
        )
    }

    return(
        <View style={{backgroundColor: "white", height: 500}}>
            <Modal
                animationType="slide"
                visible={modalVisible}
                transparent={true}
                onRequestClose={() => setModalVisible(false)}
            >
                {checkOutModal()}
            </Modal>
        </View>
    )
}



const styles = StyleSheet.create({
    modalContainer: {
        flex: 1,
        justifyContent: "flex-end",
        backgroundColor: "rgba(0,0,0,0.7)",
        height: 300
    },
    modalCheckoutContainer: {
        backgroundColor: "white",
        height: 1000, //////////////
        paddingTop: 20
    },
    
    img:{
        width: "70%",
        height: 200,
        margin: "auto",
        borderRadius: 6,
        marginBottom: 10
    },
    bag: {
        color: "black",
        textAlign: "center"
    },
    change: {
        fontSize: 20,
        fontWeight: 800
    }, 
    quantity: {
        fontSize: 20
    }
})







