    //console.log(route.params.item.img)
    const [quantity, setQuantity] = useState(1)

    return (
        <ScrollView vertical style={{backgroundColor: "white"}}>
                <Image 
                source={{uri: `https://i.ibb.co/PGWZZKD/one.jpg`}}
                style = {styles.image}
                />
                <View style={{padding: 10}}> 
                    <Text style={{fontSize: 20, fontWeight: 600, marginButtom: 15}}>Demin jeans</Text>
                    <Text style={{fontSize: 18, marginBottom: 20, fontWeight: 100}}>Lorem ipsum dolor sit amet consectetur adipisicing elit. Dolorum rem enim ratione odio animi quod facere eos. Accusantium amet iste magni. Repellendus ipsam voluptatum quae blanditiis assumenda omnis debitis magni!</Text>
                    <Text style={{fontSize: 20, fontWeight: 600, marginBottom: 15}}>$ 20</Text>
                    <View style={{flexDirection: "row"}}>
                        <Text style={{fontSize: 18, fontWeight: 100}}>Color <View ></View> <View></View> </Text>
                    </View>
                    <View style={{flexDirection: "row", justifyContent: "space-evenly", marginTop: 20, width: "100%"}}>
                        <View style={{flexDirection: "row", }}>
                            <TouchableOpacity><Text style = {styles.change}>â€”</Text></TouchableOpacity>
                            <TouchableOpacity><TextInput style={styles.quantity} onChangeText={() => {}} value= {quantity} placeholder = "1" /></TouchableOpacity>
                            <TouchableOpacity><Text style = {styles.change}>+</Text></TouchableOpacity>
                        </View>
                        <View><Text style={styles.cart} >Add to cart</Text></View>
                    </View>
                </View>
            
        </ScrollView> 
    )
}  

const styles = StyleSheet.create({
    image: {
        width: "100%",
        height: 300,
        marginBottom: 15
    },
    change: {
    },
    quantity: {
        width: "1%"
    },
    cart: {
        fontSize:20,
        marginTop: 4,
        fontWeight: 100,
        border: "1px solid black",
        borderRadius: 5,
        paddingRight: 5,
        paddingLeft: 5
    },
})
