import React from "react";
import { View, Text, SafeAreaView, ScrollView, StyleSheet, Image } from "react-native";
import { popularProducts } from "../../data";
import Product from "./Product";



export default function Products ({page}) {
    //console.log(popularProducts[0].img)
    const conditional = () => {

        if (page === "home") {
            <ScrollView horizontal showsHorizontalScrollIndicator = {false} style = {{marginLeft: 10}}>
            {popularProducts.map((item) => (
                <Product item={item} key = {item.id}  page = "home" />
            ))}
            </ScrollView>
        }
        else if (page === "productList") {
            <ScrollView vertical showsVerticalScrollIndicator = {false} style = {{marginLeft: 10}}>
            {popularProducts.map((item) => (
                <Product item={item} key = {item.id} page = "productList" />
            ))}
            </ScrollView>
        }
    }

    if (page === "home")
    return(
        <ScrollView horizontal showsHorizontalScrollIndicator = {false} style = {{marginLeft: 10}}>
        {popularProducts.map((item) => (
            <Product item={item} key = {item.id}  page = "home" />
        ))}
        </ScrollView>   
    )

    else if (page === "productList") {
        <ScrollView vertical showsVerticalScrollIndicator = {false} style = {{marginLeft: 10}}>
            {popularProducts.map((item) => (
                <Product item={item} key = {item.id} page = "productList" />
            ))}
            </ScrollView>
    }
}




