import React from "react";
import { View, Text, SafeAreaView, ScrollView, StyleSheet, Image, Button } from "react-native";



export default function ({item}) {

    return (
        <View>
            <Image
            source = {{uri: item.img}} 
            style = {{width: "100%"}}/>
            <View>
                <Text>{item.title}</Text>
                <Button>SHOP NOW</Button>
            </View>
        </View>
    )
}
