import React from "react";
import { View, Text, SafeAreaView, ScrollView, StyleSheet, Image } from "react-native";

import * as Animatable from 'react-native-animatable';
import { useSelector } from "react-redux";


export default function Side () {

    const open = useSelector((state) => state.side.open)

    return(
        <Animatable.View animation = {open ? fade : null} style={styles.main}>
            <Text>jhgfd</Text>
        </Animatable.View>
    )
}

const fade = {
    easing: "ease-out",
    
    0: {
        opacity: 0,
        translateX: 0,
      },
      0.6: {
        opacity: 1,
        translateX: 100,
      },
      0.8: {
        translateX: 200,
      },
      1: {
        opacity: 1,
        translateX: 300,
      },
    
    // 0: {
    //     translateX: "10vw"
    // },

    // 0.5: {
    //     translateX: "45vw"
    // },

    // 1: {
    //     ttranslateX: "90vw"
    // }
}

const styles = StyleSheet.create({
    main: {
        position: 'absolute',
        height: "100vh",
        width: 300,
        backgroundColor: "teal",
        right: "100vw"
    }
})
