import React, { useState } from "react";
import { View, Text, SafeAreaView, ScrollView, StyleSheet, Image, Button, TouchableOpacity, FlatList } from "react-native";


export default function Product ({route}) {
    //const {item} = route.params
    //console.log(route.params.item.img)
    return (
        <ScrollView>
                <Image 
                source={{uri: `https://i.ibb.co/PGWZZKD/one.jpg`}}
                style = {styles.image}
                />
                <View>
                    <Text style={{fontSize: 20, fontWeight: 600, marginButtom: 15}}>Demin jeans</Text>
                    <Text>Lorem ipsum dolor sit amet consectetur adipisicing elit. Dolorum rem enim ratione odio animi quod facere eos. Accusantium amet iste magni. Repellendus ipsam voluptatum quae blanditiis assumenda omnis debitis magni!</Text>
                    <Text>$ 20</Text>
                    <View style={styles.filter}>
                        <Text>Color <View></View> <View></View> </Text>
                    </View>
                    <View style={styles.cart}>
                        <View>
                            <TouchableOpacity><Text>-</Text></TouchableOpacity>
                            <TouchableOpacity><Text>1</Text></TouchableOpacity>
                            <TouchableOpacity><Text>+</Text></TouchableOpacity>
                        </View>
                    </View>
                </View>
            
        </ScrollView> 
    )
}  

const styles = StyleSheet.create({
    image: {
        width: "100%",
        height: 300,
        marginBottom: 15
    }
})
