    const handleScroll = () => {
        console.log("It works")
    }
 
    

    return (
        <ScrollView showsHorizontalScrollIndicator = {true} onScroll = {() => handleScroll}>
            
                <View style = {{
                    backgroundColor: "#f5fafd",
                    height: "270px",
